import {useState, useEffect} from 'react'
import Player from './Player';
import tachyons from 'tachyons';
import './player.css'


function AppPlay() {
  const [Songs] = useState([
    {
        title:'blessed',
        artist: 'french kiwi',
        img_src: './images/kay-the-great.jpg',
        src:'./music/FKJ_-_Blessed(128k).mp3',
    },
    {
        title:'canggu',
        artist: 'french kiwi',
        img_src: './images/kay-the-great.jpg',
        src:'./music/FKJ_-_Canggu(128k).mp3',
    },
    {
        title:'joy',
        artist: 'french kiwi',
        img_src: './images/kay-the-great.jpg',
        src:'./music/FKJ_-_Joy(128k).mp3',
    },
  ])
    const [currentSongIndex, setCurrentSongIndex] = useState(0);
    const [nextSongIndex, setNextSongIndex] = useState(currentSongIndex + 1);
    return (
        <div className='app'>
           <Player song={songs[currentSongIndex]} nextSong={songs[nextSongIndex]}/>
        </div>
    )
}

export default AppPlay
